mapper.select:
SELECT date_type AS dateType
FROM t_day
WHERE date = #{date}

SELECT DATE_FORMAT(date, '%Y-%m-%d 09:00:00') AS todayNine
FROM t_day
WHERE date = #{date}

SELECT count(date_type)
FROM t_day
WHERE date_type IN ('休息日','节假日')
        AND date
    BETWEEN #{beginDate}
        AND #{endDate}
