mapper.select:
SELECT m_event_type AS mEventType
FROM t_event_type
WHERE event_classification_level_2 = #{eventClassificationLevel2}
	AND event_classification_level_3 = #{eventClassificationLevel3};

SELECT s_event_type AS sEventType
FROM t_event_type
WHERE event_classification_level_2 = #{eventClassificationLevel2}
	AND event_classification_level_3 = #{eventClassificationLevel3};
